import React, { useEffect, useState } from 'react';
import { getUserProfile, getUserBookings } from '../services/api';
import { useAuth } from './authcontext';

const UserDashboard = () => {
  const { user } = useAuth();
  const [profile, setProfile] = useState(null);
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    if (!user) return;

    const fetchUserProfile = async () => {
      const userData = await getUserProfile(user.userId); // Using user.userId
      setProfile(userData);
    };

    const fetchUserBookings = async () => {
      const bookingsData = await getUserBookings(user.userId); // Using user.userId
      setBookings(bookingsData);
    };

    fetchUserProfile();
    fetchUserBookings();
  }, [user]);

  if (!user || !profile) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>User Dashboard</h1>
      <div>
        <h2>Profile</h2>
        <p><strong>Name:</strong> {profile.firstName} {profile.lastName}</p>
        <p><strong>Email:</strong> {profile.email}</p>
        <p><strong>Role:</strong> {profile.role}</p>
      </div>
      <div>
        <h2>Bookings</h2>
        {bookings.length === 0 ? (
          <p>No bookings found.</p>
        ) : (
          <ul>
            {bookings.map((booking) => (
              <li key={booking._id}>
                <p><strong>Property:</strong> {booking.property.title}</p>
                <p><strong>Status:</strong> {booking.status}</p>
                <p><strong>Start Date:</strong> {new Date(booking.startDate).toLocaleDateString()}</p>
                <p><strong>End Date:</strong> {new Date(booking.endDate).toLocaleDateString()}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default UserDashboard;
